import React, { useState } from 'react';
import { LocalNotifications } from '@capacitor/local-notifications';
import { Share } from '@capacitor/share';
import { Camera, CameraResultType } from '@capacitor/camera';

function App() {
  const [height, setHeight] = useState<number | undefined>(undefined);
  const [weight, setWeight] = useState<number | undefined>(undefined);
  const [bmi, setBmi] = useState<number | null>(null);
  const [bmiCategory, setBmiCategory] = useState<string>('');
  const [imageSrc, setImageSrc] = useState<string | null>(null);

  const calculateBMI = async () => {
    if (height && weight) {
      const heightInMeters = height / 100;
      const calculatedBmi = weight / (heightInMeters * heightInMeters);
      setBmi(calculatedBmi);

      let category = '';
      if (calculatedBmi < 18.5) {
        category = 'Gầy';
      } else if (calculatedBmi >= 18.5 && calculatedBmi < 25) {
        category = 'Bình thường';
      } else if (calculatedBmi >= 25 && calculatedBmi < 30) {
        category = 'Thừa cân';
      } else {
        category = 'Béo phì';
      }
      setBmiCategory(category);

      // Local Notification
      await LocalNotifications.schedule({
        notifications: [
          {
            title: 'Chỉ số BMI của bạn',
            body: `BMI: ${calculatedBmi.toFixed(2)}, ${category}`,
            id: 1,
            schedule: { at: new Date(Date.now() + 1000) },
          },
        ],
      });
    }
  };

  const shareBMI = async () => {
    if (bmi !== null) {
      await Share.share({
        title: 'Chỉ số BMI',
        text: `BMI: ${bmi.toFixed(2)}, ${bmiCategory}`,
        dialogTitle: 'Chia sẻ kết quả BMI',
      });
    }
  };

  const takePhoto = async () => {
    const photo = await Camera.getPhoto({
      resultType: CameraResultType.Uri,
    });
    if (photo.webPath) {
      setImageSrc(photo.webPath);
    } else {
      setImageSrc(null); // Hoặc bạn có thể xử lý trường hợp không có webPath theo cách khác
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Tính BMI</h1>
      <input
        type="number"
        placeholder="Chiều cao (cm)"
        value={height === undefined ? '' : height}
        onChange={(e) => setHeight(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Cân nặng (kg)"
        value={weight === undefined ? '' : weight}
        onChange={(e) => setWeight(Number(e.target.value))}
      />
      <button onClick={calculateBMI}>Tính BMI</button>
      {bmi !== null && (
        <div>
          <h2>BMI: {bmi.toFixed(2)}</h2>
          <p>Phân loại: {bmiCategory}</p>
          <button onClick={shareBMI}>Chia sẻ kết quả</button>
          <button onClick={takePhoto}>Chụp ảnh</button>
          {imageSrc && <img src={imageSrc} alt="Ảnh chụp" style={{ maxWidth: '200px' }} />}
        </div>
      )}
    </div>
  );
}

export default App;
