import React, { useState } from 'react';
import { Share } from '@capacitor/share';
import { LocalNotifications } from '@capacitor/local-notifications';

function App() {
  const [yearOfBirth, setYearOfBirth] = useState('');
  const [age, setAge] = useState<number | null>(null);


  const calculateAge = () => {
    const currentYear = new Date().getFullYear();
    const calculatedAge = currentYear - parseInt(yearOfBirth);
    setAge(calculatedAge);

    // Gửi thông báo cục bộ
    LocalNotifications.schedule({
      notifications: [
        {
          title: 'Kết quả tính tuổi',
          body: `Bạn ${calculatedAge} tuổi!`,
          id: 1,
          schedule: { at: new Date(Date.now() + 1000) },
        },
      ],
    });
  };

  const shareResult = async () => {
    if (age !== null) {
      await Share.share({
        title: 'Kết quả tính tuổi',
        text: `Tôi ${age} tuổi!`,
        dialogTitle: 'Chia sẻ kết quả',
      });
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Ứng dụng tính tuổi</h1>
      <input
        type="number"
        placeholder="Nhập năm sinh"
        value={yearOfBirth}
        onChange={(e) => setYearOfBirth(e.target.value)}
      />
      <br /><br />
      <button onClick={calculateAge}>Tính tuổi</button>
      {age !== null && (
        <div>
          <p>Bạn {age} tuổi.</p>
          <button onClick={shareResult}>Chia sẻ kết quả</button>
        </div>
      )}
    </div>
  );
}

export default App;
